_start_c():
     560:    6118                    ld    a4,0(a0)
     562:    df010113              addi    sp,sp,-528
     566:    862a                    mv    a2,a0
     568:    2705                    addiw    a4,a4,1
     56a:    00371793              slli    a5,a4,0x3
